mapper.select:
    	SELECT COUNT(*)
		FROM V_BOARD
		WHERE BOARD_TYPE = #{boardType}

mapper.update:
    	UPDATE BOARD
		SET READ_COUNT = READ_COUNT+1
		WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
SET BOARD_STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

